={{ {
  "lead_list": [
    {
      "first_name": $('Google Sheets3').item.json['first name'],
      "last_name": $('Google Sheets3').item.json['last name'],
      "email": $('Google Sheets3').item.json.Emails,
      "company_name": $('Google Sheets3').item.json['company name'],
      "website": $json.Website,
      "location": $('Google Sheets3').item.json.country,
      "custom_fields": {
        "video": $json['Video Html'],
        "icebreaker": $('Icebreakers').item.json.output
      },
      "linkedin_profile": $json['linkedin url'],
      "company_url": $json.company_linkedin
    }
  ],
  "settings": {
    "ignore_global_block_list": true,
    "ignore_unsubscribe_list": true,
    "ignore_duplicate_leads_in_other_campaign": false
  }
}
}}

={
    "campaignId": 115122,
    "accountLeadPairs": [
        {
            "linkedInAccountId": 71043,
            "lead": {
                "firstName": "{{ $json['first name'] }}",
                "lastName": "{{ $json['last name'] }}",
                "location": "{{ $json.country }}",
                "summary": "{{ $json.title }} @ {{ $json['company name'] }}",
                "companyName": "{{ $json['company name'] }}",
                "position": "{{ $json.title }}",
                "about": "{{ $json['company description'] }}",
                "emailAddress": "{{ $json.Emails }}",
                "profileUrl": "https://{{ $json['linkedin url'] }}"
            }
        }
    ]
}

